public class SimpleQueueTest {

    @Test
    public void testEnqueueAndSize() {
        SimpleQueue<Integer> queue = new SimpleQueue<>();
        assertEquals(0, queue.size());

        queue.enqueue(10);
        queue.enqueue(20);
        assertEquals(2, queue.size());
    }

    @Test
    public void testDequeue() {
        SimpleQueue<String> queue = new SimpleQueue<>();
        queue.enqueue("A");
        queue.enqueue("B");

        assertEquals("A", queue.dequeue());
        assertEquals("B", queue.dequeue());
        assertTrue(queue.isEmpty());
    }

    @Test
    public void testPeek() {
        SimpleQueue<Integer> queue = new SimpleQueue<>();
        assertNull(queue.peek()); // 空队列返回 null

        queue.enqueue(5);
        assertEquals(5, queue.peek());
        assertEquals(1, queue.size()); // peek 不移除元素
    }

    @Test
    public void testIsEmpty() {
        SimpleQueue<Double> queue = new SimpleQueue<>();
        assertTrue(queue.isEmpty());

        queue.enqueue(3.14);
        assertFalse(queue.isEmpty());
    }

    @Test
    public void testDequeueEmptyQueueThrowsException() {
        SimpleQueue<Integer> queue = new SimpleQueue<>();
        assertThrows(NoSuchElementException.class, queue::dequeue);
    }

    @Test
    public void testEnqueueNullThrowsException() {
        SimpleQueue<String> queue = new SimpleQueue<>();
        assertThrows(IllegalArgumentException.class, () -> queue.enqueue(null));
    }
}
